sys_ui_action.script:
gs.info('test');
try {


    var inputs = {};
    inputs['productoffering'] = current;

    var externalId = current.external_id;
    var state = current.status;
    gs.info('externalId' + externalId);
    if (state == 'published') {

        if (externalId == '') {

            var result = sn_fd.FlowAPI.getRunner().subflow('global.post_salesforce_flow').inForeground().withInputs(inputs).run();
            var outputs = result.getOutputs();
            gs.info('outputs' + outputs);
        } else {

            var result1 = sn_fd.FlowAPI.getRunner().subflow('global.patch_salceforce_flow').inForeground().withInputs(inputs).run();
            var outputs1 = result1.getOutputs();
            gs.info('output1'+outputs1);
        }
    } else if (state == 'retired') {
        var result2 = sn_fd.FlowAPI.getRunner().subflow('global.patch_salceforce_flow').inForeground().withInputs(inputs).run();
        var outputs2 = result2.getOutputs();
    }
	else {
		gs.addInfoMessage('Kindly Publish the Product Offering');
	}

    // Start Asynchronously: Uncomment to run in background.
    // sn_fd.FlowAPI.startFlow('main_flow', inputs);
} catch (ex) {
    var message = ex.getMessage();
    gs.error(message);
}
